const USER_INFO_KEY = 'p2p_user_info';

class AuthService {
  getCurrentUser() {
    const userInfoStr = localStorage.getItem(USER_INFO_KEY);
    if (userInfoStr) {
      try {
        return JSON.parse(userInfoStr);
      } catch (error) {
        console.error('Error parsing user info:', error);
        return null;
      }
    }
    return null;
  }

  setCurrentUser(userInfo) {
    localStorage.setItem(USER_INFO_KEY, JSON.stringify(userInfo));
  }

  logout() {
    localStorage.removeItem(USER_INFO_KEY);
  }

  async generateInvitationCode() {
    console.log('Generating invitation code...');
    try {
      // 使用当前网页的主机名构建API URL
      const currentHost = window.location.hostname;
      // 如果是本地主机，使用localhost:5235，否则使用当前主机名配合5235端口
      const apiUrl = currentHost === 'localhost' ? 
        'http://localhost:5235' : 
        `http://${currentHost}:5235`;
      
      console.log(`Using API URL: ${apiUrl}`);
      
      const response = await fetch(`${apiUrl}/api/invitation/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        mode: 'cors'
      });

      console.log('Response status:', response.status);
      
      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error response:', errorText);
        throw new Error(`Failed to generate invitation code: ${response.status} ${errorText}`);
      }

      const data = await response.json();
      console.log('Response data:', data);
      return data;
    } catch (error) {
      console.error('Error generating code:', error);
      throw error;
    }
  }

  async authenticateWithCode(invitationCode) {
    console.log('Authenticating with code:', invitationCode);
    try {
      // 使用当前网页的主机名构建API URL
      const currentHost = window.location.hostname;
      // 如果是本地主机，使用localhost:5235，否则使用当前主机名配合5235端口
      const apiUrl = currentHost === 'localhost' ? 
        'http://localhost:5235' : 
        `http://${currentHost}:5235`;
        
      console.log(`Using API URL: ${apiUrl}`);
      
      const response = await fetch(`${apiUrl}/api/invitation/authenticate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
        mode: 'cors',
        body: JSON.stringify({ invitationCode: invitationCode.trim() })
      });

      console.log('Auth response status:', response.status);
      
      if (!response.ok) {
        const errorText = await response.text();
        console.error('Auth error response:', errorText);
        try {
          const errorData = JSON.parse(errorText);
          throw new Error(errorData.message || 'Authentication failed');
        } catch (e) {
          throw new Error(`Authentication failed: ${response.status} ${errorText}`);
        }
      }

      const data = await response.json();
      console.log('Auth response data:', data);
      return data;
    } catch (error) {
      console.error('Error authenticating:', error);
      throw error;
    }
  }
}

// 创建实例并导出，避免匿名默认导出
const authService = new AuthService();
export default authService;
