const USER_INFO_KEY = 'p2p_user_info';

class AuthService {
  getCurrentUser() {
    const userInfoStr = localStorage.getItem(USER_INFO_KEY);
    if (userInfoStr) {
      try {
        return JSON.parse(userInfoStr);
      } catch (error) {
        console.error('Error parsing user info:', error);
        return null;
      }
    }
    return null;
  }

  setCurrentUser(userInfo) {
    localStorage.setItem(USER_INFO_KEY, JSON.stringify(userInfo));
  }

  logout() {
    localStorage.removeItem(USER_INFO_KEY);
  }

  async generateInvitationCode() {
    console.log('Generating invitation code...');
    try {
      const response = await fetch('http://localhost:5235/api/invitation/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }
      });

      console.log('Response status:', response.status);
      
      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error response:', errorText);
        throw new Error(`Failed to generate invitation code: ${response.status} ${errorText}`);
      }

      const data = await response.json();
      console.log('Response data:', data);
      return data;
    } catch (error) {
      console.error('Error generating code:', error);
      throw error;
    }
  }

  async authenticateWithCode(invitationCode) {
    const response = await fetch('/api/invitation/authenticate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ invitationCode })
    });

    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Authentication failed');
    }

    return await response.json();
  }
}

export default new AuthService();
